import React from 'react'
import axios from 'axios'

import FoodPairing from './FoodPairing'

import './SearchBeer.css'

class SearchBeer extends React.Component {
	state = {
		post: [],
		abvLevel: "4",
		verso: true,
		card: undefined
	};

	componentDidMount() {
		this.getBeers()
	}

	getBeers = () => {
		axios.get('https://api.punkapi.com/v2/beers')
			.then(res =>
				this.setState({ post: res.data })
			)
	}

	filterRange = (e) => {
		this.setState({ abvLevel: parseInt(e.target.value) })
	}

	getBeerAbv = () => {
		this.state.post.filter(item => item.abv > this.state.abvLevel)
	}

	handleClick = (e) => {

		// this.setState({ card: e.target.id })
		if (this.state.card = e.target.id) {
			this.setState({ verso: !this.state.verso })
		}
	}

