import React from 'react'
import Proptypes from 'prop-types'

const FoodPairing = (props) => {

  state = {
    card: false
  }

  const handleClick = (e) => {
    setState({ card: !state.card })
    console.log(card)
  }

  const { item, index } = props
  return (
    <div id={index}
      className={this.state.verso ? "recto" : 'verso'}
      key={index}
    >
      {/* {this.state.card !== index ? ( */}
      {!this.state.verso ? (
        <div className="beer-abv" id={index} onClick={(e) => handleClick(e)}>
          <h2 className="beer-title">{item.name}</h2>
          <img className="photo_beer" id={index} src={item.image_url} alt={item.name} />
          <div className="name-abv">
            <h3>{item.abv} % vol</h3>
          </div>
        </div>
      ) : (
          <div className="center-food" id={index} onClick={(e) => handleClick(e)}>
            <h2 className="beer-title">{item.name} </h2>
            {/* <img className="photo_beer" src={item.image_url} /> */}
            <h3>{item.abv}% vol</h3>
            <h3>Food pairing:</h3>
            <p className="food_pairing">{item.food_pairing}</p>
          </div>
        )
      }
    </div>
  )

}

FoodPairing.prototype = {
  in: oneOfType(
    Proptypes.number.isRequired,
    Proptypes.string.isRequired
  ),
  item: object.isRequired
}

export default FoodPairing
