import React from 'react'
import axios from 'axios'

import './SearchBeer.css'

class SearchBeer extends React.Component {
	state = {
		post: [],
		abvLevel: "4",
		verso: true,
		card: undefined
	};

	componentDidMount() {
		this.getBeers()
	}

	getBeers = () => {
		axios.get('https://api.punkapi.com/v2/beers')
			.then(res =>
				this.setState({ post: res.data })
			)
	}

	filterRange = (e) => {
		this.setState({ abvLevel: parseInt(e.target.value) })
	}

	getBeerAbv = () => {
		this.state.post.filter(item => item.abv > this.state.abvLevel)
	}

	handleClick = (e) => {
		let cardId = this.state.card
		if (cardId = e.target.id) {
			this.setState({ card: e.target.id })
			this.setState({ verso: !this.state.verso })
		}
	}

	// changeVerso = () => {
	// 	console.log("ca marche")
	// 	this.setState({ verso: !this.state.verso })
	// }

	render() {
		console.log(this.state.card, "cardSelected")
		return (
			<>
				<div className="beer-container">
					<div className="beer-range">
						<label>How strong do you like your beer ?</label>
						<p>more than {this.state.abvLevel} % vol</p>
						<input type="range"
							className="abv-range"
							min="4"
							max="15"
							step="1"
							value={this.state.abvLevel}
							onChange={(e) => this.filterRange(e)} />
					</div>
				</div>
				{this.getBeerAbv()}
				{this.state.post
					.filter(beer => beer.abv > this.state.abvLevel)
					.map((item, index) => (
						<div id={index}
							className={this.state.verso ? "recto" : 'verso'}
							key={index}
						>
							{/* {this.state.card !== index ? ( */}
							{!this.state.verso ? (
								<div className="beer-abv" id={index} onClick={(e) => this.handleClick(e)}>
									<h2 className="beer-title">{item.name}</h2>
									<img className="photo_beer" id={index} src={item.image_url} alt={item.name} />
									<div className="name-abv">
										<h3>{item.abv} % vol</h3>
									</div>
								</div>
							) : (
									<div className="center-food" id={index} onClick={(e) => this.handleClick(e)}>
										<h2 className="beer-title">{item.name} </h2>
										{/* <img className="photo_beer" src={item.image_url} /> */}
										<h3>{item.abv}% vol</h3>
										<h3>Food pairing:</h3>
										<p className="food_pairing">{item.food_pairing}</p>
									</div>
								)
							}
						</div>
					))
				}
			</>
		);
	}
}

export default SearchBeer
