import React from 'react'

class Calculator extends React.Component {
        state = {
            userWeight: "",
            weightToReach: ""
        }


    handleChange = (event) => {
           this.setState({userWeight : event.target.value})  
    }
    getWeightToReach = () => {
        this.setState({weightToReach: this.state.userWeight *2})
    }

    render() {
        return (
        <div>
            <form>
                <label>Enter your Weight
                    <input 
                        className="userWeight" 
                        value={this.state.userWeight} 
                        onChange={this.handleChange}>
                    </input>
                </label>
                <p>Poids actuel: {this.state.userWeight}</p>
                <button onClick={getWeightToReach}> Calcul you weight !</button>            
                <p>calculator{this.state.weightToReach}</p> 
            </form>
        </div>
        )
    }
}

export default Calculator
