import React from 'react'

class Calculator extends React.Component {
        state = {
            userWeight: null,
            weightToReach: null
        }


    handleChange = (event) => {
           this.setState({userWeight : event.target.value})  
    }


    getWeightToReach = () => {
        if(typeof userWeight === 'number'){
            this.setState({weightToReach: this.state.userWeight + 10})
        } else {
            return 'Enter a correct number';
        }
    }

    render() {
        return (
        <div>
            <form>
                <label>Enter your Weight
                    <input 
                        className="userWeight" 
                        value={this.state.userWeight} 
                        onChange={this.handleChange}>
                    </input>
                </label>
                <p>Poids actuel: {this.state.userWeight}</p>
                <input type="button" value='push' onClick= {() => this.getWeightToReach(this.state.userWeight)}></input>            
                <p>calculator{this.state.weightToReach}</p> 
            </form>
        </div>
        )
    }
}

export default Calculator
