import React from 'react'

class Calculator extends React.Component {
        state = {
            userWeight: null,
            weightToReach: null
        }


    handleChange = (event) => {
           this.setState({userWeight : event.target.value})  
    }


    getWeightToReach = (userWeight) => {
        const num = parseInt(this.state.userWeight)
        console.log(typeof userWeight)
        if(typeof userWeight != num){
            return {weightToReach : 'Enter a correct number'};
        } 
        else {
             this.setState({weightToReach: parseInt(this.state.userWeight)*0.2})
        }
    }

    render() {  
        return (
        <div>
            <form>
                <label>Enter your Weight
                    <input 
                        className="userWeight" 
                        value={this.state.userWeight} 
                        onChange={this.handleChange}>
                    </input>
                </label>
                <p>Poids actuel: {this.state.userWeight}</p>
                <input type="button" value='push' onClick= {() => this.getWeightToReach(this.state.userWeight)}></input>            
                <p>calculator {this.state.weightToReach}</p> 
            </form>
        </div>
        )
    }
}

export default Calculator
