import React from 'react'

class Calculator extends React.Component {
    // constructor(props){
    //     super(props)
        state = {
            weightToReach: ''
        }


    handleChange = (event) => {
           this.setState({weightToReach : event.target.value})  
    }


    // calculator = (event) => {
    //     this.setState({weightToReach : event.target.value*2})  
    // }

    // getWeight = () => {
    //     this.handleChange()
    // }

    render() {
        return (
        <div>
            <form>
                <label>Enter your Weight
                    <input 
                        className="userWeight" 
                        value={this.state.weightToReach} 
                        onChange={this.handleChange}>
                    </input>
                </label>
                <p>Poids actuel: {this.state.weightToReach}</p>
                <p>Poids futur: {this.state.weightToReach}</p>                
            </form>
        </div>
        )
    }
}

export default Calculator
