import React from 'react'

class Calculator extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            weightToReach: ''
        }
    }



    handleChange = (event) => {
           this.setState({weightToReach : event.target.value})  
    }

    calculator = () => {
        this.handleChange()
    }

    // getWeight = () => {
    //     this.handleChange()
    // }

    render() {
        return (
        <div>
            <form>
                <label>Enter your Weight
                    <input 
                        className="userWeight" 
                        value={this.state.weightToReach} 
                        onChange={this.handleChange}>
                    </input>
                </label>
                <p>Poids actuel: {this.state.weightToReach}</p>
                <p>Poids rêvé: {this.state.calculator}</p>
            </form>
        </div>
        )
    }
}

export default Calculator
