import React from 'react'

class Calculator extends React.Component {
    // constructor(props){
    //     super(props)
        state = {
            weight: "",
            weightToReach: ""
        }


    handleChange = (event) => {
           this.setState({weight : event.target.value})  
    }
    // getWeightToReach = () => {
    //     this.handleChange()
    // }

    render() {
        return (
        <div>
            <form>
                <label>Enter your Weight
                    <input 
                        className="userWeight" 
                        value={this.state.weight} 
                        onChange={this.handleChange}>
                    </input>
                </label>
                <p>Poids actuel: {this.state.weight}</p>
                <button className='calculator' onClick=''>Poids futur: {this.state.weightToReach}</button>             
            </form>
        </div>
        )
    }
}

export default Calculator
